import React, { useEffect } from 'react';
import lightsabersLit from '../../media/lightsabers-lit.png';
import './Cards.css';
// import { loadData } from './MovieCardsSlice'


const MovieCards = (props) => {

    const FavouritesComponent = props.favouritesComponent;

    return (
        <div className="card-container" >
            {props.loaded === true ?
                props.movies.map((title, index) => {
                    return (
                        <div key={'cardKey' + index} id={'movieCard' + index}>
                            <div className="card" >
                                <h2>{title}</h2>
                                <img src={lightsabersLit} className="lightsabersLit" />
                                <div onclick={props.handleFavouritesClick} className='overlay'><FavouritesComponent /></div>
                            </div>

                        </div>
                    )
                })
                : <h1>Loading...</h1>
            }
        </div>
    )
}

export default MovieCards;
