import React, { useEffect, useState } from "react";
import axios from 'axios';

import './Components/Cards/Cards.css';
import MovieCards from './Components/MovieCards/MovieCards';
import SearchTerm from './Components/Features/SearchTerm/SearchTerm';

import './App.css';
// import { Cards } from './Components/Cards/Cards';
// import Films from './swapiDataFetch';


function App(props) {

  const { state, dispatch } = props;

  const [Data, setData] = useState({
    Titles: [],
    Loaded: false
  })

  // const [searchValue, setSearchValue] = useState('');

  // const visibleAllFilms = getFilteredFilms(Data, state.searchTerm);
  // const visibleFavoriteRecipes = getFilteredFilms(state.favoriteRecipes, state.searchTerm);

  useEffect(() => {
    axios.get('https://www.swapi.tech/api/films/')
      .then(res => {
        let swapiData = res.data.result;

        setData({
          Titles: swapiData.map((x) => {
            return x.properties.title
          }),
          Loaded: true
        }
        )

      })
      .catch(err => {
        console.log(err);
      })
  }, [])

  return (
    <div className="App">

      <main>
        <section>
          <h2>My favourites:</h2>
          <div className="favorites card-container">
            <p>//RENDER FAVOURITE CARDS</p>
          </div>
          <h2>Search Movie List:</h2>
          {/* SEARCH TERM TO PASS DATA TO COMPONENT */}
          <SearchTerm
            searchTerm={state.searchTerm}
            // setSearchValue={setSearchValue} 
            dispatch={dispatch}
          />
          <h2>Swapi Movies:</h2>
          <div className='filmsArray card-container'>
            {/* MOVIE ARRAY TO PASS DATA TO COMPONENT */}
            <MovieCards
              movies={Data.Titles}
              oaded={Data.Loaded} />
          </div>
        </section>
      </main >
    </div >
  );
}

/* Utility Helpers */

function getFilteredFilms(recipes, searchTerm) {
  return recipes.filter(recipe => recipe.name.toLowerCase().includes(searchTerm.toLowerCase()));
}

export default App;
