import React, { useEffect, useState } from "react";
import axios from 'axios';
import lightsabersLit from './media/lightsabers-lit.png';
import './Components/Cards/Cards.css';




const Films = () => {

    const [Data, setData] = useState({
        Titles: [],
        Loaded: false
    })

    useEffect(() => {
        axios.get('https://www.swapi.tech/api/films/')
            .then(res => {
                let swapiData = res.data.result;
                setData({
                    // Films: swapiData.forEach(element => { console.log(element.properties.title) }) 
                    //REFACTOR*****************************
                    Titles: [swapiData[0].properties.title, swapiData[1].properties.title, swapiData[2].properties.title, swapiData[3].properties.title, swapiData[4].properties.title, swapiData[5].properties.title],
                    Loaded: true
                })
            })
            .catch(err => {
                console.log(err);
            })
    }, [])


    function handleSubmit(e) {
        e.preventDefault();
        console.log('you clicked me')
    }


    return (

        <div className="card-container" >
            {Data.Loaded === true ?
                Data.Titles.map((title, index) => {
                    return (
                        <div key={'cardKey' + index}>
                            <div className="card" >
                                <h2>{title}</h2>
                                <button onClick={handleSubmit}>
                                    <img src={lightsabersLit} className="lightsabersLit" />
                                </button>
                            </div>
                        </div>

                    )
                })

                : <h1>Loading...</h1>
            }
        </div>

    )

}
export default Films;
