import React, { useEffect, useState } from "react";
import axios from 'axios';

import './Components/Cards/Cards.css';
import MovieCards from './Components/MovieCards/MovieCards';
import SearchBox from './Components/Features/SearchBox/SearchBox';

import lightsabersLit from './media/lightsabers-lit.png';
import './App.css';
import { Cards } from './Components/Cards/Cards';
import Films from './swapiDataFetch';


function App() {

  const [Data, setData] = useState({
    Titles: [],
    Loaded: false
  })

  const [searchValue, setSearchValue] = useState('')

  useEffect(() => {
    axios.get('https://www.swapi.tech/api/films/')
      .then(res => {
        let swapiData = res.data.result;

        setData({
          Titles: swapiData.map((x) => {
            return x.properties.title
          }),
          Loaded: true
        }
        )

      })
      .catch(err => {
        console.log(err);
      })
  }, [])

  return (
    <div className="App">

      <main>
        <section>
          <h2>My favourites:</h2>
          <div className="favorites card-container">
            <p>empty</p>
          </div>
          <h2>Swapi Movies:</h2>
          <MovieCards movies={Data.Titles} loaded={Data.Loaded} />

          <div className='filmsArray card-container'>
            <MovieCards movies={Data.Titles} loaded={Data.Loaded} />
          </div>
        </section>
      </main >
    </div >
  );
}

export default App;
