import allTitlesData from '../../../Data'

export const loadData = () => {
    return {
        type: 'allTitles/loadData',
        payload: allTitlesData
    }
}

const initialState = [];
export const allTitlesReducer = (allTitles = initialState, action) => {
    switch (action.type) {
        case 'allTitles/loadData':
            return action.payload;
        case 'favoriteTitles/addTitle':
            return allTitles.filter(title => title.id !== action.payload.id);
        case 'favoriteTitles/removeTitle':
            return [...allTitles, action.payload]
        default:
            return allTitles;
    }
}
