import React, { useEffect, useState } from "react";
import axios from 'axios';

import './Components/Cards/Cards.css';
import MovieCards from './Components/MovieCards/MovieCards';
import SearchTerm from './Components/Features/SearchTerm/SearchTerm';
import AddFavourite from './Components/AddFavourites/AddFavourites';
import RemoveFavourites from "./Components/RemoveFavourites/RemoveFavourites";

import './App.css';


function App() {

  const [Data, setData] = useState({
    Titles: [],
    Loaded: false
  })

  const [searchValue, setSearchValue] = useState('');

  const [favourites, setFavourites] = useState([]);

  // const visibleAllFilms = getFilteredFilms(Data.Titles, state.searchTerm);
  // const visibleFavoriteRecipes = getFilteredFilms(state.favoriteRecipes, state.searchTerm);

  useEffect(() => {
    axios.get('https://www.swapi.tech/api/films/')
      .then(res => {
        let swapiData = res.data.result;

        setData({
          Titles: swapiData.map((x) => {
            return x.properties.title
          }),
          Loaded: true
        }
        )

      })
      .catch(err => {
        console.log(err);
      })
  }, [])

  const saveToLocalStorage = (items) => {
    localStorage.setItem('react-movie-app-favourites', JSON.stringify(items))
  }

  const addFavouriteMovie = (movie) => {
    if ([...favourites].includes(movie)) {
      return;
    } else {
      const newFavouriteList = [...favourites, movie];
      setFavourites(newFavouriteList);
      saveToLocalStorage(newFavouriteList);
    }
  }

  const removeFavouriteMovie = (movie) => {
    const newFavouriteList = favourites.filter((favourite) => favourite !== movie);
    setFavourites(newFavouriteList);
  }

  return (
    <div className="App">

      <main>
        <section>

          <h2>My favourites:</h2>
          <div className="favorites card-container">
            <p>//RENDER FAVOURITE CARDS</p>
          </div>
          <h2>Search Movie List:</h2>
          <SearchTerm
            searchValue={searchValue}
            setSearchValue={setSearchValue}
          />
          <h2>Swapi Movies:</h2>
          <div className='filmsArray card-container'>
            <MovieCards
              movies={Data.Titles}
              loaded={Data.Loaded}
              handleFavouritesClick={addFavouriteMovie}
              favouritesComponent={AddFavourite}
            />
          </div>
          <h2>Dynam Favourites:</h2>
          <div className='filmsArray card-container'>
            <MovieCards
              movies={favourites}
              loaded={Data.Loaded}
              handleFavouritesClick={removeFavouriteMovie}
              favouritesComponent={RemoveFavourites}
            />
          </div>
        </section>
      </main >
    </div >
  );
}

export default App;
